INSERT INTO `ecs_article` ( `article_id`, `cat_id`, `title`, `content`, `author`, `author_email`, `keywords`, `article_type`, `is_open`, `add_time`, `file_url`, `open_type`, `link`, `description` ) VALUES  ('12', '5', '注册会员', '<p>&nbsp;</p>\r\n<p align=\"left\" style=\"padding-bottom: 0px; widows: 2; text-transform: none; background-color: rgb(255,255,255); text-indent: 24px; margin: 0px; padding-left: 0px; padding-right: 0px; font: 14px/28px Arial, Helvetica, sans-serif; white-space: normal; orphans: 2; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; padding-top: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\"><strong style=\"line-height: 45px; font-size: 14px\">&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;</strong>\r\n<p>\r\n<table border=\"0\" cellspacing=\"0\" cellpadding=\"5\" width=\"100%\" align=\"center\" style=\"text-align: left; padding-bottom: 0px; widows: 2; text-transform: none; background-color: rgb(255,255,255); text-indent: 24px; margin: 0px; padding-left: 0px; width: 768px; padding-right: 0px; border-collapse: collapse; font: 14px/28px Arial, Helvetica, sans-serif; white-space: normal; orphans: 2; table-layout: fixed; letter-spacing: normal; color: rgb(51,51,51); word-spacing: 0px; padding-top: 0px; -webkit-text-size-adjust: auto; -webkit-text-stroke-width: 0px\">\r\n    <tbody>\r\n        <tr style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">\r\n            <td bgcolor=\"#cccccc\" width=\"9%\" style=\"border-bottom: rgb(153,153,153) 1px solid; text-align: center; border-left: rgb(153,153,153) 1px solid; padding-bottom: 5px; line-height: 20px; margin: 0px; padding-left: 5px; padding-right: 5px; font-family: Arial, Helvetica, sans-serif; word-wrap: break-word; font-size: 12px; border-top: rgb(153,153,153) 1px solid; border-right: rgb(153,153,153) 1px solid; padding-top: 5px\">\r\n            <p align=\"center\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 14px; padding-top: 0px\">会员级别</p>\r\n            </td>\r\n            <td bgcolor=\"#cccccc\" width=\"91%\" style=\"border-bottom: rgb(153,153,153) 1px solid; text-align: center; border-left: rgb(153,153,153) 1px solid; padding-bottom: 5px; line-height: 20px; margin: 0px; padding-left: 5px; padding-right: 5px; font-family: Arial, Helvetica, sans-serif; word-wrap: break-word; font-size: 12px; border-top: rgb(153,153,153) 1px solid; border-right: rgb(153,153,153) 1px solid; padding-top: 5px\">\r\n            <p align=\"center\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 14px; padding-top: 0px\">如何获取会员资格</p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">\r\n            <td style=\"border-bottom: rgb(153,153,153) 1px solid; text-align: center; border-left: rgb(153,153,153) 1px solid; padding-bottom: 5px; line-height: 20px; margin: 0px; padding-left: 5px; padding-right: 5px; font-family: Arial, Helvetica, sans-serif; word-wrap: break-word; font-size: 12px; border-top: rgb(153,153,153) 1px solid; border-right: rgb(153,153,153) 1px solid; padding-top: 5px\">\r\n            <p class=\"color_blur\" align=\"center\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; color: rgb(0,102,204); font-size: 14px; padding-top: 0px\">普通会员</p>\r\n            </td>\r\n            <td style=\"border-bottom: rgb(153,153,153) 1px solid; text-align: center; border-left: rgb(153,153,153) 1px solid; padding-bottom: 5px; line-height: 20px; margin: 0px; padding-left: 5px; padding-right: 5px; font-family: Arial, Helvetica, sans-serif; word-wrap: break-word; font-size: 12px; border-top: rgb(153,153,153) 1px solid; border-right: rgb(153,153,153) 1px solid; padding-top: 5px\">\r\n            <p align=\"left\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 14px; padding-top: 0px\">&nbsp;&nbsp;&nbsp; 注册并购买本商城诚品产品，即可成为本商城诚品普通会员。第一次购物即可享受积分和后续的积分奖励。</p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">\r\n            <td style=\"border-bottom: rgb(153,153,153) 1px solid; text-align: center; border-left: rgb(153,153,153) 1px solid; padding-bottom: 5px; line-height: 20px; margin: 0px; padding-left: 5px; padding-right: 5px; font-family: Arial, Helvetica, sans-serif; word-wrap: break-word; font-size: 12px; border-top: rgb(153,153,153) 1px solid; border-right: rgb(153,153,153) 1px solid; padding-top: 5px\">\r\n            <p class=\"color_blur\" align=\"center\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; color: rgb(0,102,204); font-size: 14px; padding-top: 0px\">VIP会员</p>\r\n            </td>\r\n            <td style=\"border-bottom: rgb(153,153,153) 1px solid; text-align: center; border-left: rgb(153,153,153) 1px solid; padding-bottom: 5px; line-height: 20px; margin: 0px; padding-left: 5px; padding-right: 5px; font-family: Arial, Helvetica, sans-serif; word-wrap: break-word; font-size: 12px; border-top: rgb(153,153,153) 1px solid; border-right: rgb(153,153,153) 1px solid; padding-top: 5px\">\r\n            <p align=\"left\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 14px; padding-top: 0px\">&nbsp;&nbsp;&nbsp; VIP会员资格，可以通过以下方式获得：</p>\r\n            <p align=\"left\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 14px; padding-top: 0px\">&nbsp;&nbsp;&nbsp; 1）普通会员一次购物超过800元，即可获得VIP会员资格，有效期1年。</p>\r\n            <p align=\"left\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 14px; padding-top: 0px\">&nbsp;&nbsp;&nbsp; 2）普通会员累计购物超过2000元，即可获得VIP会员资格，有效期1年。</p>\r\n            <p align=\"left\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 14px; padding-top: 0px\">&nbsp;&nbsp;&nbsp; 3）其他特别的促销活动，有效期1年。</p>\r\n            </td>\r\n        </tr>\r\n        <tr style=\"padding-bottom: 0px; margin: 0px; padding-left: 0px; padding-right: 0px; padding-top: 0px\">\r\n            <td style=\"border-bottom: rgb(153,153,153) 1px solid; text-align: center; border-left: rgb(153,153,153) 1px solid; padding-bottom: 5px; line-height: 20px; margin: 0px; padding-left: 5px; padding-right: 5px; font-family: Arial, Helvetica, sans-serif; word-wrap: break-word; font-size: 12px; border-top: rgb(153,153,153) 1px solid; border-right: rgb(153,153,153) 1px solid; padding-top: 5px\">\r\n            <p class=\"color_blur\" align=\"center\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; color: rgb(0,102,204); font-size: 14px; padding-top: 0px\">SVIP会员</p>\r\n            </td>\r\n            <td style=\"border-bottom: rgb(153,153,153) 1px solid; text-align: center; border-left: rgb(153,153,153) 1px solid; padding-bottom: 5px; line-height: 20px; margin: 0px; padding-left: 5px; padding-right: 5px; font-family: Arial, Helvetica, sans-serif; word-wrap: break-word; font-size: 12px; border-top: rgb(153,153,153) 1px solid; border-right: rgb(153,153,153) 1px solid; padding-top: 5px\">\r\n            <p align=\"left\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 14px; padding-top: 0px\">&nbsp;&nbsp;&nbsp; SVIP会员资格，可以通过以下方式获得：</p>\r\n            <p align=\"left\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 14px; padding-top: 0px\">&nbsp;&nbsp;&nbsp; 1）普通会员一次购物超过1500元，即可获得SVIP会员资格，有效期1年。</p>\r\n            <p align=\"left\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 14px; padding-top: 0px\">&nbsp;&nbsp;&nbsp; 2）VIP会员一次购物超过1200元，即可获得SVIP会员资格，有效期1年。</p>\r\n            <p align=\"left\" style=\"padding-bottom: 0px; line-height: 28px; margin: 0px; padding-left: 0px; padding-right: 0px; font-family: Arial, Helvetica, sans-serif; font-size: 14px; padding-top: 0px\">&nbsp;&nbsp;&nbsp; 3）VIP会员在有效期内累计购物超过2500元，即可获得SVIP会员资格，有效期1年。</p>\r\n            <p align=\"left\" style=\"padding-bottom: 0px; line-height: 28px; ma